import React, { Component } from 'react';
import { Form, Button } from "react-bootstrap";
import { Link } from 'react-router-dom';

class ChatPage extends Component {

    /**
     * Funkce přihlásí uživatele.
     * 
     * @param {*} event 
     */
    submitHandler = (event) => {
        event.preventDefault();

        const email = this.emailElement.current.value;
        const password = this.passwordElement.current.value;

        const requestBody = {
            query: `
                query {
                    login(loginInput: {email: "${email}", password: "${password}"}) {
                        userId
                        email
                        token
                        tokenExpiration
                        households
                    }
                }
            `
        };
        fetch('http://localhost:5000/graphql', {
            method: 'POST',
            body: JSON.stringify(requestBody),
            headers: {
                'content-type': 'application/json'
            }
        })
            .then(res => {   
                // if (res.status !== 200 && res.status !== 201) {}                           
                return res.json();
            })
            .then(resData => { 
                if(resData.errors){
                    throw new Error(resData.errors[0].message); // provizorní
                }
                return resData.data;
            })
            .then(validData => { 
                this.context.login(validData.login.token, validData.login.userId, validData.login.email, validData.login.tokenExpiration, validData.login.households[0])
            })
            .catch(err => {
                console.log(err.message);
            });
    };

    render() {
        return (
            <Form className="col-md-3">
                <Form.Group className="mb-3" controlId="formEmail">
                    <Form.Label>text</Form.Label>
                    <Form.Control type="email" placeholder="E-mail" />                    
                </Form.Group>
                <Button variant="primary" type="submit">Odeslat</Button>
            </Form>
        );
    }
}

export default ChatPage;
